ude_tts_debug.log","originalFile":"#!/bin/bash\n\n# Enhanced TTS script that speaks the last Claude response\necho \"$(date): Enhanced TTS hook triggered\" >> /tmp/claude_tts_debug.log\n\n# Read JSON input from stdin\ninput=$(cat)\necho \"Input received: $input\" >> /tmp/claude_tts_debug.log\n\n# Extract transcript_path from JSON input\ntranscript_path=$(echo \"$input\" | jq -r '.transcript_path // empty')\necho \"Transcript path: $transcript_path\" >> /tmp/claude_tts_debug.log\n\nif [[ -n \"$transcript_path\" && -f \"$transcript_path\" ]]; then\n    # Extract the last assistant message from the transcript\n    last_response=$(jq -r '.messages[] | select(.role == \"assistant\") | .content' \"$transcript_path\" | tail -1)\n    \n    echo \"Last response extracted: ${last_response:0:100}...\" >> /tmp/claude_tts_debug.log\n    \n    if [[ -n \"$last_response\" ]]; then\n        # Clean up the response text for TTS\n        # Remove markdown formatting, tool calls, and limit length\n        clean_text=$(echo \"$last_response\" | \\\n            sed 's/<function_calls>.*<\\/antml:function_calls>//g' | \\\n            sed 's/```[^`]*```//g' | \\\n            sed 's/\\*\\*\\([^*]*\\)\\*\\*/\\1/g' | \\\n            sed 's/\\*\\([^*]*\\)\\*/\\1/g' | \\\n            sed 's/`\\([^`]*\\)`/\\1/g' | \\\n            tr '\\n' ' ' | \\\n            head -c 500)\n        \n        echo \"Clean text for TTS: $clean_text\" >> /tmp/claude_tts_debug.log\n        \n        # Speak the last response\n        if [[ -n \"$clean_text\" && \"$clean_text\" != \"null\" ]]; then\n            nohup say \"$clean_text\" > /dev/null 2>&1 &\n            echo \"TTS executed successfully\" >> /tmp/claude_tts_debug.log\n        else\n            # Fallback message\n            nohup say \"Claude response complete\" > /dev/null 2>&1 &\n            echo \"Fallback TTS executed\" >> /tmp/claude_tts_debug.log\n        fi\n    else\n        # Fallback if no response found\n        nohup say \"Claude response complete\" > /dev/null 2>&1 &\n        echo \"No response found, fallback TTS executed\" >> /tmp/claude_tts_debug.log\n    fi\nelse\n    # Fallback if no transcript available\n    nohup say \"Claude response complete\" > /dev/null 2>&1 &\n    echo \"No transcript available, fallback TTS executed\" >> /tmp/claude_tts_debug.log\nfi","structuredPatch":[{"oldStart":12,"oldLines":8,"newStart":12,"newLines":22,"lines":[" echo \"Transcript path: $transcript_path\" >> /tmp/claude_tts_debug.log"," "," if [[ -n \"$transcript_path\" && -f \"$transcript_path\" ]]; then","-    # Extract the last assistant message from the transcript","-    last_response=$(jq -r '.messages[] | select(.role == \"assistant\") | .content' \"$transcript_path\" | tail -1)","+    # Handle both JSON and JSONL formats","+    if [[ \"$transcript_path\" == *.jsonl ]]; then","+        # JSONL format - each line is a separate JSON object","+        last_response=$(tail -20 \"$transcript_path\" | jq -r 'select(.type == \"assistant_message\") | .content // empty' | tail -1)","+        if [[ -z \"$last_response\" || \"$last_response\" == \"null\" ]]; then","+            # Try alternative structure","+            last_response=$(tail -20 \"$transcript_path\" | jq -r 'select(.role == \"assistant\") | .content // empty' | tail -1)","+        fi","+        if [[ -z \"$last_response\" || \"$last_response\" == \"null\" ]]; then","+            # Try another structure - look for assistant responses in conversation","+            last_response=$(tail -20 \"$transcript_path\" | jq -r '.messages[]? | select(.role == \"assistant\") | .content // empty' | tail -1)","+        fi","+    else","+        # JSON format","+        last_response=$(jq -r '.messages[] | select(.role == \"assistant\") | .content' \"$transcript_path\" | tail -1)","+    fi","     ","     echo \"Last response extracted: ${last_response:0:100}...\" >> /tmp/claude_tts_debug.log","     "]}],"userModified":false,"replaceAll":false}}
